import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { MessageCircle, Send, User, ArrowLeft } from 'lucide-react';

const MessagesPage = ({ onNavigate }) => {
  const { currentUser } = useAuth();
  const [conversations, setConversations] = useState([]);
  const [currentChat, setCurrentChat] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading conversations
    setTimeout(() => {
      setConversations([
        {
          id: 1,
          user: {
            id: 2,
            name: "Sarah Chen",
            photo: null,
            isOnline: true
          },
          lastMessage: "Thanks for the feedback on my latest work!",
          timestamp: new Date(Date.now() - 3600000).toISOString(),
          unread: true
        },
        {
          id: 2,
          user: {
            id: 3,
            name: "Marcus Rodriguez",
            photo: null,
            isOnline: false
          },
          lastMessage: "I'd love to collaborate on a music project",
          timestamp: new Date(Date.now() - 7200000).toISOString(),
          unread: false
        }
      ]);
      setIsLoading(false);
    }, 1000);
  }, []);

  const handleSendMessage = () => {
    if (!newMessage.trim() || !currentChat) return;

    const message = {
      id: Date.now(),
      content: newMessage,
      senderId: currentUser.id,
      receiverId: currentChat.user.id,
      timestamp: new Date().toISOString(),
      isSent: true
    };

    setMessages([...messages, message]);
    setNewMessage('');

    // Update conversation list
    const updatedConversations = conversations.map(conv => 
      conv.id === currentChat.id 
        ? { ...conv, lastMessage: newMessage, timestamp: new Date().toISOString() }
        : conv
    );
    setConversations(updatedConversations);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  const startChat = (conversation) => {
    setCurrentChat(conversation);
    // Load messages for this conversation
    setMessages([
      {
        id: 1,
        content: "Hi! I loved your latest artwork",
        senderId: conversation.user.id,
        receiverId: currentUser.id,
        timestamp: new Date(Date.now() - 3600000).toISOString(),
        isSent: false
      },
      {
        id: 2,
        content: conversation.lastMessage,
        senderId: currentUser.id,
        receiverId: conversation.user.id,
        timestamp: conversation.timestamp,
        isSent: true
      }
    ]);
  };

  if (!currentUser) {
    return (
      <div className="text-center py-12">
        <div className="text-6xl mb-4">🔒</div>
        <p className="text-gray-500 mb-4">Please log in to view messages</p>
        <button 
          onClick={() => onNavigate('login')}
          className="bg-purple-600 hover:bg-purple-700 text-white px-6 py-3 rounded-lg transition-colors"
        >
          Login
        </button>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="spinner"></div>
        <span className="ml-3 text-gray-600">Loading messages...</span>
      </div>
    );
  }

  return (
    <div className="fade-in">
      <div className="bg-white rounded-xl shadow-lg p-8">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">💬 Messages</h2>
        
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Chat List */}
          <div className="lg:col-span-1">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold">Conversations</h3>
              <button 
                onClick={() => window.location.reload()}
                className="text-gray-500 hover:text-gray-700 p-2 rounded-lg transition-colors"
              >
                <ArrowLeft className="w-4 h-4" />
              </button>
            </div>
            
            <div className="space-y-2 max-h-96 overflow-y-auto">
              {conversations.length > 0 ? (
                conversations.map(conversation => (
                  <div 
                    key={conversation.id}
                    onClick={() => startChat(conversation)}
                    className={`p-4 border rounded-lg cursor-pointer hover:bg-gray-50 transition-colors ${
                      currentChat?.id === conversation.id ? 'bg-purple-50 border-purple-200' : ''
                    }`}
                  >
                    <div className="flex items-center space-x-3">
                      {conversation.user.photo ? (
                        <img 
                          src={conversation.user.photo} 
                          alt={conversation.user.name} 
                          className="w-10 h-10 rounded-full object-cover"
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.nextSibling.style.display = 'flex';
                          }}
                        />
                      ) : (
                        <div className="w-10 h-10 rounded-full bg-purple-100 flex items-center justify-center">
                          <User className="text-purple-600" />
                        </div>
                      )}
                      <div className="w-10 h-10 rounded-full bg-purple-100 flex items-center justify-center" style={{ display: 'none' }}>
                        <User className="text-purple-600" />
                      </div>
                      
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center justify-between">
                          <h4 className="font-medium text-gray-800 truncate">
                            {conversation.user.name}
                          </h4>
                          {conversation.unread && (
                            <div className="w-2 h-2 bg-purple-500 rounded-full"></div>
                          )}
                        </div>
                        <p className="text-sm text-gray-600 truncate">
                          {conversation.lastMessage}
                        </p>
                        <p className="text-xs text-gray-400">
                          {new Date(conversation.timestamp).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="text-center py-8">
                  <div className="text-4xl mb-2">💬</div>
                  <p className="text-gray-500 text-sm">No conversations yet</p>
                  <button 
                    onClick={() => onNavigate('explore')}
                    className="mt-3 bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg text-sm transition-colors"
                  >
                    Find Artists to Chat
                  </button>
                </div>
              )}
            </div>
          </div>
          
          {/* Chat Window */}
          <div className="lg:col-span-2">
            <div className="border rounded-xl h-96 flex flex-col">
              {currentChat ? (
                <>
                  <div className="bg-gray-50 p-4 border-b rounded-t-xl">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center">
                        {currentChat.user.photo ? (
                          <img 
                            src={currentChat.user.photo} 
                            alt={currentChat.user.name} 
                            className="profile-photo mr-3"
                            onError={(e) => {
                              e.target.style.display = 'none';
                              e.target.nextSibling.style.display = 'flex';
                            }}
                          />
                        ) : (
                          <div className="profile-photo bg-purple-100 flex items-center justify-center mr-3">
                            <User className="text-purple-600" />
                          </div>
                        )}
                        <div className="profile-photo bg-purple-100 flex items-center justify-center mr-3" style={{ display: 'none' }}>
                          <User className="text-purple-600" />
                        </div>
                        <div>
                          <h4 className="font-semibold text-gray-800">{currentChat.user.name}</h4>
                          <p className="text-sm text-gray-500">
                            {currentChat.user.isOnline ? '🟢 Online' : '⚫ Offline'}
                          </p>
                        </div>
                      </div>
                      <button 
                        onClick={() => onNavigate('artist-profile', currentChat.user.id)}
                        className="text-gray-500 hover:text-gray-700 px-3 py-1 rounded-lg transition-colors"
                      >
                        👤 View Profile
                      </button>
                    </div>
                  </div>
                  
                  <div className="flex-1 p-4 overflow-y-auto space-y-3">
                    {messages.length > 0 ? (
                      messages.map(message => (
                        <div key={message.id} className={`flex ${message.isSent ? 'justify-end' : 'justify-start'}`}>
                          <div className={`chat-bubble ${message.isSent ? 'sent text-white' : 'received'} px-4 py-2 rounded-lg`}>
                            <p>{message.content}</p>
                            <p className="text-xs opacity-75 mt-1">
                              {new Date(message.timestamp).toLocaleTimeString()}
                            </p>
                          </div>
                        </div>
                      ))
                    ) : (
                      <div className="text-center py-8">
                        <div className="text-4xl mb-2">👋</div>
                        <p className="text-gray-500">Start your conversation!</p>
                      </div>
                    )}
                  </div>
                  
                  <div className="border-t p-4">
                    <div className="flex space-x-2">
                      <input 
                        type="text" 
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        onKeyPress={handleKeyPress}
                        placeholder="Type your message..." 
                        className="flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:border-purple-500 form-input"
                      />
                      <button 
                        onClick={handleSendMessage}
                        className="bg-purple-600 hover:bg-purple-700 text-white px-6 py-2 rounded-lg transition-colors"
                      >
                        <Send className="w-4 h-4" />
                      </button>
                    </div>
                  </div>
                </>
              ) : (
                <div className="flex-1 flex items-center justify-center">
                  <div className="text-center">
                    <MessageCircle className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-500">Select a conversation to start chatting</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MessagesPage;
