import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { User, Mail, Lock, Palette, X } from 'lucide-react';

const RegisterModal = ({ onClose, onNavigate }) => {
  const { signup } = useAuth();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    category: 'Visual Arts',
    photo: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      console.log('Starting registration process...');
      console.log('Form data:', formData);
      
      await signup(
        formData.email, 
        formData.password, 
        formData.name, 
        formData.photo || null, 
        formData.category
      );
      
      console.log('Registration successful!');
      onClose();
      // Show success notification
    } catch (error) {
      console.error('Registration error:', error);
      console.error('Error code:', error.code);
      console.error('Error message:', error.message);
      
      if (error.code === 'auth/email-already-in-use') {
        setError('Email already exists! Please use a different email or try logging in.');
      } else if (error.code === 'auth/weak-password') {
        setError('Password is too weak. Please use at least 6 characters.');
      } else if (error.code === 'auth/invalid-email') {
        setError('Invalid email address. Please check your email format.');
      } else if (error.code === 'auth/network-request-failed') {
        setError('Network error. Please check your internet connection.');
      } else {
        setError(`Registration failed: ${error.message}. Please try again.`);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const categories = [
    { value: 'Music', label: '🎵 Music' },
    { value: 'Visual Arts', label: '🎨 Visual Arts' },
    { value: 'Writing', label: '✍️ Writing' },
    { value: 'Dance', label: '💃 Dance' },
    { value: 'Photography', label: '📸 Photography' },
    { value: 'Video Production', label: '🎬 Video Production' },
    { value: 'Voice Acting', label: '🎤 Voice Acting' },
    { value: 'Graphic Design', label: '🖌️ Graphic Design' },
    { value: 'Digital Art', label: '💻 Digital Art' },
    { value: 'Animation', label: '🎞️ Animation' }
  ];

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-xl p-8 max-w-md w-full mx-4 fade-in">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold text-center">🚀 Join KalaKriti v5.0</h2>
          <button 
            onClick={onClose}
            className="text-gray-500 hover:text-gray-700"
          >
            <X className="w-5 h-5" />
          </button>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2 flex items-center">
              <User className="w-4 h-4 mr-2" />
              👤 Full Name
            </label>
            <input 
              type="text" 
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:border-purple-500 form-input" 
              required 
            />
          </div>
          
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2 flex items-center">
              <Mail className="w-4 h-4 mr-2" />
              📧 Email
            </label>
            <input 
              type="email" 
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:border-purple-500 form-input" 
              required 
            />
          </div>
          
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2 flex items-center">
              <Palette className="w-4 h-4 mr-2" />
              🎨 Primary Art Category
            </label>
            <select 
              name="category"
              value={formData.category}
              onChange={handleChange}
              className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:border-purple-500 form-input" 
              required
            >
              {categories.map(category => (
                <option key={category.value} value={category.value}>
                  {category.label}
                </option>
              ))}
            </select>
          </div>
          
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2">
              📸 Profile Photo URL (Optional)
            </label>
            <input 
              type="url" 
              name="photo"
              value={formData.photo}
              onChange={handleChange}
              placeholder="https://example.com/your-photo.jpg"
              className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:border-purple-500 form-input" 
            />
          </div>
          
          <div className="mb-6">
            <label className="block text-gray-700 text-sm font-bold mb-2 flex items-center">
              <Lock className="w-4 h-4 mr-2" />
              🔒 Password
            </label>
            <input 
              type="password" 
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:border-purple-500 form-input" 
              required 
            />
          </div>
          
          {error && (
            <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-lg text-sm">
              {error}
            </div>
          )}
          
          <button 
            type="submit" 
            disabled={isLoading}
            className="w-full bg-purple-600 hover:bg-purple-700 disabled:bg-purple-400 text-white font-bold py-3 px-4 rounded-lg transition-colors"
          >
            {isLoading ? 'Creating Account...' : 'Create Account'}
          </button>
        </form>
        
        <div className="mt-4 text-center">
          <p className="text-gray-600">Already have an account?</p>
          <button 
            onClick={() => onNavigate('login')}
            className="text-purple-600 hover:text-purple-700 font-medium"
          >
            Sign in here
          </button>
        </div>
      </div>
    </div>
  );
};

export default RegisterModal;
