import React, { useState } from 'react';
import { auth, db } from '../firebase/config';
import { collection, getDocs } from 'firebase/firestore';

const FirebaseTest = () => {
  const [testResult, setTestResult] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const testFirebaseConnection = async () => {
    setIsLoading(true);
    setTestResult('Testing Firebase connection...');
    
    try {
      // Test Firestore connection
      const usersRef = collection(db, 'users');
      const snapshot = await getDocs(usersRef);
      console.log('Firestore test successful. Users count:', snapshot.size);
      
      // Test Auth connection
      console.log('Auth object:', auth);
      console.log('Auth app:', auth.app);
      
      setTestResult(`✅ Firebase connection successful! 
        - Firestore: Connected (${snapshot.size} users found)
        - Auth: Connected
        - Project ID: ${auth.app.options.projectId}`);
        
    } catch (error) {
      console.error('Firebase test error:', error);
      setTestResult(`❌ Firebase connection failed: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="fixed top-4 right-4 bg-white p-4 rounded-lg shadow-lg border z-50">
      <h3 className="font-bold mb-2">Firebase Test</h3>
      <button 
        onClick={testFirebaseConnection}
        disabled={isLoading}
        className="bg-blue-500 text-white px-3 py-1 rounded text-sm mb-2"
      >
        {isLoading ? 'Testing...' : 'Test Connection'}
      </button>
      <div className="text-xs text-gray-600 max-w-xs">
        {testResult}
      </div>
    </div>
  );
};

export default FirebaseTest;
